<?php

namespace KG\BeekeepingManagementBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * RucheRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RucheRepository extends EntityRepository
{
    public function getRucheByRucher($rucher)
    {
        return $this->createQueryBuilder('ruche')
                    ->leftJoin('ruche.emplacement','emplacement')
                    ->leftJoin('emplacement.rucher','rucher')
                    ->addSelect('emplacement')
                    ->addSelect('rucher')
                    ->where('rucher.id = :rucher')
                    ->setParameter('rucher',$rucher)
                    ->getQuery()
                    ->getResult();
    }       
}
